//{Valid:true
//IsLocal:false}
func TestValidateAddress(t *testing.T) {
	validate, err := akcSdk.ValidateAddress("m01qsumvgtvacsadnk0q45c5vnyjrymufxc9k35t8l")
	assert.Nil(t, err, err)
	assert.True(t, validate.Valid)
	fmt.Println(fmt.Sprintf("%+v", validate))
}

func TestNewAccount(t *testing.T) {
	newAccount, err := akcSdk.NewAccount()
	assert.Nil(t, err, err)
	fmt.Println(newAccount.GetAddress())
	fmt.Println(newAccount.GetPrivateKey())
	fmt.Println(newAccount.GetPublicKey())
	fmt.Println(newAccount.GetControlProgram())
	fmt.Println(newAccount.GetAccountID())
	fmt.Println(newAccount.GetAccountAlias())
}

func TestRecoveryAccount(t *testing.T) {
	xpubs := []string{"c536a2c11fafd8278e02e9393dcbf5aa420eb51a1761a7e5da7f2b9b37969b52a8f8e2b692e7dcaf79dfa0d1e28c63eb9fda42942f20feaa8a71b383d9a4668c"}
	err := akcSdk.RecoveryAccount(xpubs)
	assert.Nil(t, err, err)
}

func TestListPubKeys(t *testing.T) {
	accountID := "0x34b78a41c237E4F8953732B9A446a5eEd362A96c"
	pubKeys, err := akcSdk.ListPubKeys(accountID)
	assert.Nil(t, err, err)
	fmt.Println(fmt.Sprintf("%+v", pubKeys))
}

func TestAccountSerialization(t *testing.T) {
	a, err := akcSdk.NewAccount()
	assert.Nil(t, err, err)
	accountStr, err := a.ToString()
	assert.Nil(t, err, err)

	loadedAccount, err := account.LoadAcount(accountStr)
	assert.Nil(t, err, err)
	assert.Equal(t, a.GetAccountID(), loadedAccount.GetAccountID())
	assert.Equal(t, a.GetAddress(), loadedAccount.GetAddress())
	assert.Equal(t, a.GetPrivateKey(), loadedAccount.GetPrivateKey())
	assert.Equal(t, a.GetAccountAlias(), loadedAccount.GetAccountAlias())
	assert.Equal(t, a.GetControlProgram(), loadedAccount.GetControlProgram())
	assert.Equal(t, a.GetPublicKey(), loadedAccount.GetPublicKey())
}

func TestNewAccountFromPrivateKey(t *testing.T) {
	x, _ := hex.DecodeString("a0051340fc3406e38d51e02b2fbbb8e8e16f7bc7c5597e55c514213b60d608587f123c0621a4d38387b892522777f1ee47d7d8c2601a17a7d7db163053c0f28d")
	var xprv chainkd.XPrv
	copy(xprv[:], x[:64])
	accountFromPrivateKey, err := akcSdk.NewAccountFromPrivateKey(xprv)
	assert.Nil(t, err, err)
	assert.Equal(t, "a6013acf14cc938de89e0dedea229c499fa9ff95775d90061ddbe7ab192ff2157f123c0621a4d38387b892522777f1ee47d7d8c2601a17a7d7db163053c0f28d",
		accountFromPrivateKey.GetPublicKey())
	assert.Equal(t, "a0051340fc3406e38d51e02b2fbbb8e8e16f7bc7c5597e55c514213b60d608587f123c0621a4d38387b892522777f1ee47d7d8c2601a17a7d7db163053c0f28d",
		accountFromPrivateKey.GetPrivateKey())
	fmt.Println(accountFromPrivateKey.GetAddress())
	fmt.Println(accountFromPrivateKey.GetPrivateKey())
	fmt.Println(accountFromPrivateKey.GetPublicKey())
	fmt.Println(accountFromPrivateKey.GetControlProgram())
	fmt.Println(accountFromPrivateKey.GetAccountID())
	fmt.Println(accountFromPrivateKey.GetAccountAlias())
}

