func TestListAccounts(t *testing.T) {
	accounts, err := akcSdk.ListAccounts()
	assert.Nil(t, err, err)
	fmt.Println(fmt.Sprintf("%+v", accounts))
}

func TestSignVerify(t *testing.T) {
	msg, _ := hex.DecodeString("Hello world")
	newAccount, err := akcSdk.NewAccount()
	assert.Nil(t, err, err)
	sig := newAccount.Sign(msg)
	verify := newAccount.Verify(msg, sig)
	assert.True(t, verify)
}
